      useNewUrlParser: true,
      useUnifiedTopology: true,
      useCreateIndex: true,
    });
    console.log(`Database is connected to: ${db.connection.name}`);
  } catch (error) {
    console.log(`Database connection error: ${error.message}`);
  }
})();

