const router = Router();

import * as procuctsCtrl from "../controllers/products.controller";
import { authJwt } from "../middlewares";

router.post(
  "/",
  [authJwt.verifyToken, authJwt.isAdmin],
  procuctsCtrl.createProduct
);
router.get("/", procuctsCtrl.getProducts);
router.get("/:productId", procuctsCtrl.getProductById);
router.put("/:productId", authJwt.verifyToken, procuctsCtrl.updateProductById);
router.delete(
  "/:productId",
  [authJwt.verifyToken, authJwt.isAdmin],
  procuctsCtrl.deleteProductById
);

export default router;

