import { Schema, model } from "mongoose";

const productSchema = new Schema(
  {
    name: {
      type: String,
      required: true,
      trim: true,
    },
    category: String,
    price: Number,
    imgURL: String,
  },
  {
    timestamps: true,
    versionkey: false,
  }
);

export default model("Product", productSchema);

